<template>
  <div id="peliculas">
    <h2 id="subtitle h2">Películas</h2>
    <h3 id="subtitle h3" v-if="favoriteMovie">Película Favorita: {{favoriteMovie | mayuscula}}</h3>
    <Pelicula :callbackFavoriteMovie="onResultados" v-for="pelicula in peliculasTitleUpperCase" :key="pelicula.id" :image="pelicula.image" :title="pelicula.title"></Pelicula>
  </div>
</template>

<script>
import Pelicula from "@/components/Pelicula";

export default {
  name: "Peliculas",
  components: {
    Pelicula
  },
  methods: {
    onResultados(movie) {
      this.favoriteMovie = movie
    }
  },
  computed: { //propiedades computadas
    peliculasTitleUpperCase(){
      this.peliculas.map((pelicula) => {
        pelicula.title.toUpperCase()
        pelicula.title = pelicula.title.toUpperCase() + " (Propiedad Computada)"
      })
      return this.peliculas;
    }
  },
  filters: {
    mayuscula(string){
      return string.toUpperCase();
    }
  },
  data(){
    return {
      peliculas: [
        {id: 1, title: "Spiderman", image: "https://filmdaily.co/wp-content/uploads/2021/12/nowayhome-02.jpeg"},
        {id: 2, title: "Rapido y Furioso", image: "https://e00-marca.uecdn.es/assets/multimedia/imagenes/2020/03/12/15840382283314.jpg"},
        {id: 3, title: "TED", image: "data:image/jpeg;base64,"}
      ],
      favoriteMovie: ""
    }
  }
}
</script>

